/*
------------------------------------------------------------------------------------
------------------------------------------------------------------------------------
功能: 格式化字符串
用法: 与C#中的String.Format函数一样
    stringFormat(parameterFormat, userId, account, domainId);
------------------------------------------------------------------------------------
------------------------------------------------------------------------------------
*/
exports.stringFormat=function () {
    if (arguments.length == 0)
        return null;
    var str = arguments[0];
    for (var i = 1; i < arguments.length; i++) {
        var re = new RegExp('\\{' + (i - 1) + '\\}', 'gm');
        str = str.replace(re, arguments[i]);

    }

    return str;
};
/*
------------------------------------------------------------------------------------
------------------------------------------------------------------------------------
功能: 检查字符串是否为空
------------------------------------------------------------------------------------
------------------------------------------------------------------------------------
*/
exports.stringIsNullOrEmpty=function(fData){
    var isNullOrEmpty = true;
    if (fData) {
        if (typeof (fData) == 'string') {
            if (fData.length > 0)
                isNullOrEmpty = false;
        }
    }
    return isNullOrEmpty;
}